import UIKit

class CitiesRouter {

    weak var viewController: UIViewController?
}

// MARK: - CitiesWireframeInterface -
extension CitiesRouter: CitiesWireframeInterface {
    
    func navigate(to option: CitiesNavigationOption) {
        var vc: UIViewController?
        switch option {
        case .cityDeail(let weather):
            vc = CityDetailConfigurator.createModule(with: weather)
        }
        if vc != nil {
            DispatchQueue.main.async {
                self.viewController?.present(vc!, animated: true)
            }
        }
    }
}
